    function getMaxThrows(num, count, shiny) {
        var amt = maxThrows;
        var bst = getBST(num);
        if (inclusive(bst, 0, 360)) {
            amt += 7;
        } else if (inclusive(bst, 360, 420)) {
            amt += 5;
        } else if (inclusive(bst, 420, 520)) {
            amt += 3;
        } else if (!isRare(num)) {
            amt += 1;
        } else {
            amt -= 1;
        }
        amt += 2 * count;
        amt -= 2 * shiny;
        return Math.max(maxThrows, amt);
    }

