            0: 11,
            1: Math.min(10 + p, 11),
            2: Math.min(9 + p, 11),
            3: Math.min(8 + p, 11),
            4: Math.min(7 + p, 11),
            5: Math.min(6 + p, 11),
            6: Math.min(5 + p, 11),
            7: Math.min(4 + p, 11),
            8: Math.min(3 + p, 11),
            9: Math.min(2 + p, 11),
