                if (this.pyr.stamina[id] <= 0) {
                    continue;
                }
                c = id in defeated ? defeated[id].length : 0;
                dmg = Math.round(averageDamage * (1 - ((c / Math.max(defeatedCount, 1)) - 0.3)));
