        this.diffRewards = reward2 && reward !== reward2;
        this.reward2 = this.diffRewards ? reward2 : reward;
        this.rewardName = translateStuff(reward);
        this.reward2Name = translateStuff(this.reward2);
        this.hasReward = true;

