    this.giftMedal = function(src, commandData) {
        var cd = commandData.split(":");
        if (commandData.length < 3) {
            safaribot.sendMessage(src, "The format for this command is /awardmedal player:icon:description.", safchan);
            return false;
        }
        var player = getAvatarOff(cd[0]);
        if (!player) {
            safaribot.sendMessage(src, "No player exists!", safchan);
            return false;
        }
        var icon = cd[1];
        if (!icon) {
            safaribot.sendMessage(src, "That is not a valid color of medal!", safchan);
            return false
        }
        icon = parseInt(icon, 10);
        if (isNaN(icon)) {
            safaribot.sendMessage(src, "That is not a valid color number!", safchan);
            return false
        }
        var description = cd[2];
        var m = {
            desc: description,
            icon: icon
        }
        this.awardMedal(player, m);
        safaribot.sendMessage(src, "You awarded " + player.id + " the medal: " + description + "!", safchan);
        return true;
    };
    this.changeDailyBoost = function(data) {
        var randomNum, bst;

        if (data) {
            randomNum = getInputPokemon(data).num;
            bst = getBST(randomNum);
        }
        if (!randomNum) {
            do {
                randomNum = sys.rand(1, 722);
                bst = getBST(randomNum);

            } while (bst > 498 || isLegendary(randomNum));
        }

        var bonus = 1.78 - (318 - (498 - bst)) * 0.5 / 318;

        dailyBoost = {
            pokemon: randomNum,
            bonus: bonus,
            expires: now() + hours(24)
        };
        permObj.add("dailyBoost", JSON.stringify(dailyBoost));
        permObj.remove("nextDailyBoost");
        permObj.save();
        sys.sendAll(separator, safchan);
        safaribot.sendAll("The Boost-of-the-Day is now " + sys.pokemon(randomNum) + ", who will give a bonus catch rate of " + bonus.toFixed(2) + "x if used as your active Pokémon!", safchan);
        sys.sendAll(separator, safchan);
    };
